structure/notifications_profile_changes_operator_subject.txt <pre><code>    Owner details have been updated
</code></pre> structure/notifications_profile_changes_operator_message.txt <pre><code>    Owner of
    {% for o in organizations %}
        {{ o.name }} {% if o.abbreviation %} ({{ o.abbreviation }}){% endif %}{% if not forloop.last %}, {% endif %}
    {% endfor %}

    {{user.full_name}} (id={{ user.id }}) has changed

    {% for f in fields %}
        {{ f.name }} from {{ f.old_value }} to {{ f.new_value }}{% if not forloop.last %}, {% else %}.{% endif %}
    {% endfor %}
</code></pre> structure/notifications_profile_changes_operator_message.html <pre><code>    Owner of
    {% for o in organizations %}
        {{ o.name }} {% if o.abbreviation %} ({{ o.abbreviation }}){% endif %}{% if not forloop.last %}, {% endif %}
    {% endfor %}

    {{user.full_name}} (id={{ user.id }}) has changed

    {% for f in fields %}
        {{ f.name }} from {{ f.old_value }} to {{ f.new_value }}{% if not forloop.last %}, {% else %}.{% endif %}
    {% endfor %}
</code></pre>

users/invitation_approved_subject.txt <pre><code>    Account has been created
</code></pre> users/invitation_approved_message.txt <pre><code>    Hello!

    {{ sender }} has invited you to join {{ name }} {{ type }} in {{ role }} role.
    Please visit the link below to sign up and accept your invitation:
    {{ link }}

    Your credentials are as following.

    Username is {{ username }}

    Your password is {{ password }}
</code></pre> users/invitation_approved_message.html <pre><code>    &lt;html&gt;
    &lt;head lang="en"&gt;
        &lt;meta charset="UTF-8"&gt;
        &lt;title&gt;Account has been created&lt;/title&gt;
    &lt;/head&gt;
    &lt;body&gt;
    &lt;p&gt;
        Hello!
    &lt;/p&gt;
    &lt;p&gt;
        {{ sender }} has invited you to join {{ name }} {{ type }} in {{ role }} role.&lt;br&gt;
        Please visit &lt;a href="{{ link }}"&gt;this page&lt;/a&gt; to sign up and accept your invitation.
    &lt;/p&gt;
    &lt;p&gt;
      Your credentials are as following.
    &lt;/p&gt;
    &lt;p&gt;
      Your username is {{ username }}
    &lt;/p&gt;
    &lt;p&gt;
      Your password is {{ password }}
    &lt;/p&gt;
    &lt;/body&gt;
    &lt;/html&gt;
</code></pre>

users/invitation_created_subject.txt <pre><code>    {% if reminder %}
    REMINDER: Invitation to {{ name }} {{ type }}
    {% else %}
    Invitation to {{ name }} {{ type }}
    {% endif %}
</code></pre> users/invitation_created_message.txt <pre><code>    Hello!

    {{ sender }} has invited you to join {{ name }} {{ type }} in {{ role }} role.
    Please visit the link below to sign up and accept your invitation:
    {{ link }}
    {{ extra_invitation_text }}
</code></pre> users/invitation_created_message.html <pre><code>    &lt;html&gt;
    &lt;head lang="en"&gt;
        &lt;meta charset="UTF-8"&gt;
        &lt;title&gt;Invitation to {{ name }} {{ type }}&lt;/title&gt;
    &lt;/head&gt;
    &lt;body&gt;
    &lt;p&gt;
        Hello!
    &lt;/p&gt;
    &lt;p&gt;
        {{ sender }} has invited you to join {{ name }} {{ type }} in {{ role }} role.&lt;br&gt;
        Please visit &lt;a href="{{ link }}"&gt;this page&lt;/a&gt; to sign up and accept your invitation.
        Please note: this invitation expires at {{ invitation.get_expiration_time|date:'d.m.Y H:i' }}!
    &lt;/p&gt;
    &lt;p&gt;
        {{ extra_invitation_text }}
    &lt;/p&gt;
    &lt;/body&gt;
    &lt;/html&gt;
</code></pre>

users/invitation_expired_subject.txt <pre><code>    Invitation has expired
</code></pre> users/invitation_expired_message.txt <pre><code>    Hello!

